import { Channel } from "@kite9/fdc3-standard";
import { ChannelSelector } from "@kite9/fdc3-standard"
import { AbstractUIComponent } from "./AbstractUIComponent";
import { BrowserTypes } from "@kite9/fdc3-schema";

type IframeChannels = BrowserTypes.IframeChannels
type IframeChannelSelected = BrowserTypes.IframeChannelSelected

/**
 * Works with the desktop agent to provide a simple channel selector.
 * 
 * This is the default implementation, but can be overridden by app implementers calling 
 * the getAgent() method
 */
export class DefaultDesktopAgentChannelSelector extends AbstractUIComponent implements ChannelSelector {

    private callback: ((channelId: string | null) => void) | null = null

    constructor(url: string | null) {
        super(url ?? "https://fdc3.finos.org/webui/channel_selector.html", "FDC3 Channel Selector")
    }

    async setupMessagePort(port: MessagePort): Promise<void> {
        await super.setupMessagePort(port)
        this.port = port

        port.addEventListener("message", (e) => {
            if (e.data.type == 'iframeChannelSelected') {
                const choice = e.data as IframeChannelSelected
                if (this.callback) {
                    this.callback(choice.payload.selected)
                }
            }
        })
    }

    updateChannel(channelId: string | null, availableChannels: Channel[]): void {
        // also send to the iframe
        this.port!!.postMessage({
            type: 'iframeChannels',
            payload: {
                selected: channelId,
                userChannels: availableChannels.map(ch => {
                    return {
                        id: ch.id,
                        displayMetadata: ch.displayMetadata
                    }
                })
            }

        } as IframeChannels)
    }

    setChannelChangeCallback(callback: (channelId: string | null) => void): void {
        this.callback = callback
    }

}

import { IframeHello, InitialCSS, UpdatedCSS } from "@kite9/fdc3-schema/generated/api/BrowserTypes";
import { Connectable } from "@kite9/fdc3-standard";

export interface CSSPositioning { [key: string]: string }

export const INITIAL_CONTAINER_CSS = {
    width: "0",
    height: "0",
    position: "fixed"
}


export const ALLOWED_CSS_ELEMENTS = [
    "width",
    "height",
    "position",
    "z-index",
    "left",
    "right",
    "top",
    "bottom",
    "transition",
    "max-height",
    "max-width",
    "display"
]

export abstract class AbstractUIComponent implements Connectable {

    private container: HTMLDivElement | undefined = undefined
    private iframe: HTMLIFrameElement | undefined = undefined
    private url: string
    private name: string
    port: MessagePort | null = null

    constructor(url: string, name: string) {
        this.url = url
        this.name = name
    }

    async connect() {
        const portPromise = this.awaitHello()
        this.openFrame()
        this.port = await portPromise
        await this.setupMessagePort(this.port)
        await this.messagePortReady(this.port)

    }

    async disconnect() {
        this.port?.close()
    }

    /**
     * Override and extend this method to provide functionality specific to the UI in question
     */
    async setupMessagePort(port: MessagePort): Promise<void> {
        port.addEventListener("message", (e) => {
            const data = e.data
            if (data.type == 'iframeRestyle') {
                // console.log(`Restyling ${JSON.stringify(data.payload)}`)
                const css = data.payload.updatedCSS
                this.themeContainer(css)
            }
        })
    }

    async messagePortReady(port: MessagePort) {
        // tells the iframe it can start posting
        port.postMessage({ type: "iframeHandshake" })
    }

    private awaitHello(): Promise<MessagePort> {
        return new Promise((resolve, _reject) => {
            const ml = (e: MessageEvent) => {
                // console.log("Received UI Message: " + JSON.stringify(e.data))
                if ((e.source == this.iframe?.contentWindow) && (e.data.type == 'iframeHello')) {
                    const helloData = e.data as IframeHello
                    if (helloData.payload.initialCSS) {
                        this.themeContainer(helloData.payload.initialCSS)
                    }
                    const port = e.ports[0]
                    port.start()
                    globalThis.window.removeEventListener("message", ml)
                    resolve(port)
                }
            }

            globalThis.window.addEventListener("message", ml)
        });

    }

    private openFrame(): void {
        this.container = globalThis.document.createElement("div")
        this.iframe = globalThis.document.createElement("iframe")

        this.themeContainer(INITIAL_CONTAINER_CSS)
        this.themeFrame(this.iframe)

        this.iframe.setAttribute("src", this.url)
        this.container.appendChild(this.iframe)
        document.body.appendChild(this.container)
    }

    themeContainer(css: UpdatedCSS | InitialCSS) {
        if (css) {
            for (let i = 0; i < ALLOWED_CSS_ELEMENTS.length; i++) {
                const k = ALLOWED_CSS_ELEMENTS[i]
                const value: string | undefined = css[(k as string)]
                if (value != null) {
                    this.container?.style.setProperty(k, value)
                } else {
                    this.container?.style.removeProperty(k)
                }
            }
        }
    }

    themeFrame(ifrm: HTMLIFrameElement) {
        ifrm.setAttribute("name", this.name)
        ifrm.style.width = "100%"
        ifrm.style.height = "100%"
        ifrm.style.border = "0"
    }

}

import { DesktopAgent } from '@kite9/fdc3-standard'
import { getAgent } from './strategies/getAgent';
const DEFAULT_WAIT_FOR_MS = 20000;


export { getAgent }

/**
 * Replaces the original fdc3Ready function from FDC3 2.0 with a new one that uses the new getAgent function.
 * 
 * @param waitForMs Amount of time to wait before failing the promise (20 seconds is the default).
 * @returns A DesktopAgent promise.
 */
export function fdc3Ready(waitForMs = DEFAULT_WAIT_FOR_MS): Promise<DesktopAgent> {
    return getAgent({
        timeout: waitForMs,
        dontSetWindowFdc3: false,
        channelSelector: true,
        intentResolver: true
    })
}

import { DesktopAgent, GetAgentType, GetAgentParams, AgentError } from '@kite9/fdc3-standard'
import { ElectronEventLoader } from './ElectronEventLoader'
import { handleWindowProxy, PostMessageLoader } from './PostMessageLoader'
import { TimeoutLoader } from './TimeoutLoader'

const DEFAULT_WAIT_FOR_MS = 20000;

export const FDC3_VERSION = "2.2"

/**
 * For now, we only allow a single call to getAgent per application, so 
 * we keep track of the promise we use here.  
 */
var theAgentPromise: Promise<DesktopAgent> | null = null;

export function clearAgentPromise() {
    theAgentPromise = null;
}

export function getAgentPromise(): Promise<DesktopAgent> | null {
    return theAgentPromise;
}

function initAgentPromise(options: GetAgentParams): Promise<DesktopAgent> {

    const STRATEGIES = [
        new ElectronEventLoader(),
        new PostMessageLoader(),
        new TimeoutLoader()
    ]
    const promises = STRATEGIES.map(s => s.get(options));

    return Promise.race(promises)
        .then(da => {
            // first, cancel the timeout etc.
            STRATEGIES.forEach(s => s.cancel())

            // either the timeout completes first with an error, or one of the other strategies completes with a DesktopAgent.
            if (da) {
                return da as DesktopAgent
            } else {
                throw new Error(AgentError.AgentNotFound)
            }
        })
        .catch(async (error) => {
            if (options.failover) {
                const o = await handleWindowProxy(options, () => { return options.failover!!(options) })
                return o
            } else {
                throw error
            }
        })
}


/**
 * This return an FDC3 API.  Should be called by application code.
 * 
 * @param optionsOverride - options to override the default options
 */
export const getAgent: GetAgentType = (optionsOverride?: GetAgentParams) => {

    const DEFAULT_OPTIONS: GetAgentParams = {
        dontSetWindowFdc3: true,
        channelSelector: true,
        intentResolver: true,
        timeout: DEFAULT_WAIT_FOR_MS,
        identityUrl: globalThis.window.location.href
    }

    const options = {
        ...DEFAULT_OPTIONS,
        ...optionsOverride
    }

    function handleGenericOptions(da: DesktopAgent) {
        if ((!options.dontSetWindowFdc3) && (globalThis.window.fdc3 == null)) {
            globalThis.window.fdc3 = da;
            globalThis.window.dispatchEvent(new Event("fdc3Ready"));
        }

        globalThis.window.addEventListener("pagehide", () => {
            if ((da as any).disconnect) {
                (da as any).disconnect()
                theAgentPromise = null;
            }
        })

        return da;
    }

    if (!theAgentPromise) {
        theAgentPromise = initAgentPromise(options).then(handleGenericOptions)
    }

    return theAgentPromise
}

/**
 * Replaces the original fdc3Ready function from FDC3 2.0 with a new one that uses the new getAgent function.
 * 
 * @param waitForMs Amount of time to wait before failing the promise (20 seconds is the default).
 * @returns A DesktopAgent promise.
 */
export function fdc3Ready(waitForMs = DEFAULT_WAIT_FOR_MS): Promise<DesktopAgent> {
    const agent = getAgent({
        timeout: waitForMs,
        dontSetWindowFdc3: false,
        channelSelector: true,
        intentResolver: true
    })

    return agent
}

import { DesktopAgent } from "@kite9/fdc3-standard";
import { GetAgentParams } from "@kite9/fdc3-standard";
import { Loader } from "./Loader";

/**
 * This approach will resolve the loader promise if the fdc3Ready event occurs.
 * This is done by electron implementations setting window.fdc3.
 */
export class ElectronEventLoader implements Loader {

    done = false

    poll(endTime: number, resolve: (value: DesktopAgent | void) => void, reject: (reason?: any) => void) {
        const timeRemaining = endTime - Date.now()
        if (globalThis.window.fdc3 != null) {
            resolve(globalThis.window.fdc3)
        } else if ((timeRemaining > 0) && (this.done == false)) {
            setTimeout(() => this.poll(endTime, resolve, reject), 100);
        } else {
            resolve();
        }
    }

    cancel(): void {
        this.done = true;
    }

    get(params: GetAgentParams): Promise<DesktopAgent | void> {
        return new Promise<DesktopAgent | void>((resolve, reject) => {
            const endPollTime = Date.now() + (params.timeout!! + 500)
            // console.log("Starting poll: " + endPollTime + " " + params.timeout + " " + Date.now())
            this.poll(endPollTime, resolve, reject)
        });
    }
}

import { DesktopAgent, GetAgentParams } from '@kite9/fdc3-standard'
import { createDesktopAgentAPI } from '../messaging/message-port';
import { v4 as uuidv4 } from "uuid"
import { ConnectionDetails } from '../messaging/MessagePortMessaging';
import { Loader } from './Loader';
import { BrowserTypes } from "@kite9/fdc3-schema";
import { FDC3_VERSION } from './getAgent';

type WebConnectionProtocol1Hello = BrowserTypes.WebConnectionProtocol1Hello
type WebConnectionProtocol2LoadURL = BrowserTypes.WebConnectionProtocol2LoadURL
type WebConnectionProtocol3Handshake = BrowserTypes.WebConnectionProtocol3Handshake

function collectPossibleTargets(w: Window, found: Window[]) {
    if (w) {
        if (found.indexOf(w) == -1) {
            found.push(w);
        }

        if (found.indexOf(w.opener) == -1) {
            collectPossibleTargets(w.opener, found);
        }

        if (found.indexOf(w.parent) == -1) {
            collectPossibleTargets(w.parent, found);
        }
    }
}


/**
 * Starts the connection process off by sending a hello message
 */
function sendWCP1Hello(w: MessageEventSource, options: GetAgentParams, connectionAttemptUuid: string, origin: string) {
    const requestMessage: WebConnectionProtocol1Hello = {
        type: 'WCP1Hello',
        meta: {
            connectionAttemptUuid: connectionAttemptUuid,
            timestamp: new Date()
        },
        payload: {
            channelSelector: options.channelSelector,
            fdc3Version: FDC3_VERSION,
            resolver: options.intentResolver,
            identityUrl: options.identityUrl!!,
            actualUrl: globalThis.window.location.href
        }
    }

    w.postMessage(requestMessage, { targetOrigin: origin });
}

/**
 * The desktop agent requests that the client opens a URL in order to provide a message port.
 */
function openFrame(url: string): Window {
    const IFRAME_ID = "fdc3-communications-embedded-iframe"

    // remove an old one if it's there
    const existing = document.getElementById(IFRAME_ID)
    if (existing) {
        existing.remove()
    }

    // create a new one
    var ifrm = document.createElement("iframe")
    ifrm.setAttribute("src", url)
    ifrm.setAttribute("id", IFRAME_ID)
    ifrm.setAttribute("name", "FDC3 Communications")
    ifrm.style.width = "0px"
    ifrm.style.height = "0px"
    ifrm.style.border = "0"
    ifrm.style.position = "fixed"
    document.body.appendChild(ifrm)
    return ifrm.contentWindow!!
}

export function helloExchange(options: GetAgentParams, connectionAttemptUuid: string): Promise<ConnectionDetails> {

    return new Promise<ConnectionDetails>((resolve, _reject) => {
        // setup listener for message and retrieve JS URL from it
        const el = (event: MessageEvent) => {
            const data = event.data;
            if (data?.meta?.connectionAttemptUuid == connectionAttemptUuid) {
                if (data.type == 'WCP2LoadUrl') {
                    // in this case, we need to load the URL with the embedded Iframe
                    openFrame((data as WebConnectionProtocol2LoadURL).payload.iframeUrl);
                } else if (data.type == 'WCP3Handshake') {
                    resolve({
                        connectionAttemptUuid: connectionAttemptUuid,
                        handshake: data,
                        messagePort: event.ports[0],
                        options: options
                    })
                }
            }
        }

        globalThis.window.addEventListener("message", el)
    });

}

/**
 * This is a variation of the PostMessageLoader used for handling failover.  If the failover returns the WindowProxy this is used 
 * to properly load the desktop agent.
 */
export function handleWindowProxy(options: GetAgentParams, provider: () => Promise<WindowProxy | DesktopAgent>): Promise<DesktopAgent> {
    return new Promise<DesktopAgent>((resolve, _reject) => {
        const el = (event: MessageEvent) => {
            const data = event.data;
            if (data.type == 'WCP3Handshake') {
                const handshake = data as WebConnectionProtocol3Handshake;
                globalThis.window.removeEventListener("message", el)

                resolve(createDesktopAgentAPI({
                    connectionAttemptUuid: handshake.meta.connectionAttemptUuid,
                    handshake: data,
                    messagePort: event.ports[0],
                    options: options
                }))
            }
        }

        globalThis.window.addEventListener("message", el)

        provider().then((providerResult) => {
            if ((providerResult as any).getInfo) {
                globalThis.window.removeEventListener("message", el)
                resolve(providerResult as DesktopAgent)
            }
        })
    })
}


export class PostMessageLoader implements Loader {

    connectionAttemptUuid = uuidv4();

    async get(options: GetAgentParams): Promise<DesktopAgent | void> {
        const targets: Window[] = []
        collectPossibleTargets(globalThis.window, targets);

        // ok, begin the process
        const promise = helloExchange(options, this.connectionAttemptUuid)

        // use of '*': See https://github.com/finos/FDC3/issues/1316
        targets.forEach((t) => sendWCP1Hello(t, options, this.connectionAttemptUuid, "*"))

        // wait for one of the windows to return the data we need
        const data = await promise
        return createDesktopAgentAPI(data);
    }

    cancel(): void {

    }


}

import { DesktopAgent } from "@kite9/fdc3-standard";
import { BasicDesktopAgent, DefaultChannelSupport, DefaultAppSupport, DefaultIntentSupport, DefaultHandshakeSupport, ChannelSupport } from "@kite9/fdc3-agent-proxy";
import { ConnectionDetails, MessagePortMessaging } from "./MessagePortMessaging";
import { DefaultDesktopAgentIntentResolver } from "../ui/DefaultDesktopAgentIntentResolver";
import { DefaultDesktopAgentChannelSelector } from "../ui/DefaultDesktopAgentChannelSelector";
import { NullIntentResolver } from "../ui/NullIIntentResolver";
import { NullChannelSelector } from "../ui/NullChannelSelector";
import { ChannelSelector } from "@kite9/fdc3-standard";

/**
 * Given a message port, constructs a desktop agent to communicate via that.
 */
export async function createDesktopAgentAPI(cd: ConnectionDetails): Promise<DesktopAgent> {

    cd.messagePort.start()

    function string(o: string | boolean): string | null {
        if ((o == true) || (o == false)) {
            return null
        } else {
            return o
        }
    }

    const messaging = new MessagePortMessaging(cd)

    const useResolver = cd.handshake.payload.intentResolverUrl && cd.options.intentResolver
    const useSelector = cd.handshake.payload.channelSelectorUrl && cd.options.channelSelector

    const intentResolver = useResolver ?
        new DefaultDesktopAgentIntentResolver(string(cd.handshake.payload.intentResolverUrl)) :
        new NullIntentResolver()

    const channelSelector = useSelector ?
        new DefaultDesktopAgentChannelSelector(string(cd.handshake.payload.channelSelectorUrl))
        : new NullChannelSelector()

    const cs = new DefaultChannelSupport(messaging, channelSelector)
    const hs = new DefaultHandshakeSupport(messaging)
    const is = new DefaultIntentSupport(messaging, intentResolver)
    const as = new DefaultAppSupport(messaging)
    const da = new BasicDesktopAgent(hs, cs, is, as, [hs, intentResolver, channelSelector])
    await da.connect()

    await populateChannelSelector(cs, channelSelector)

    return da
}



async function populateChannelSelector(cs: ChannelSupport, channelSelector: ChannelSelector): Promise<void> {
    const channel = await cs.getUserChannel()
    const userChannels = await cs.getUserChannels()
    channelSelector.updateChannel(channel?.id ?? null, userChannels)
}

import { AbstractWebMessaging } from './AbstractWebMessaging'
import { RegisterableListener } from "@kite9/fdc3-agent-proxy"
import { GetAgentParams } from "@kite9/fdc3-standard"
import { v4 as uuidv4 } from "uuid"
import { BrowserTypes } from "@kite9/fdc3-schema";
type WebConnectionProtocol3Handshake = BrowserTypes.WebConnectionProtocol3Handshake

/**
 * Details needed to set up the Messaging instance
 */
export type ConnectionDetails = {
    connectionAttemptUuid: string
    handshake: WebConnectionProtocol3Handshake,
    messagePort: MessagePort,
    options: GetAgentParams
}

export class MessagePortMessaging extends AbstractWebMessaging {

    private readonly cd: ConnectionDetails
    private readonly listeners: Map<string, RegisterableListener> = new Map()

    constructor(cd: ConnectionDetails, deliveryTimeoutMs?: number) {
        super(cd.options, cd.connectionAttemptUuid, deliveryTimeoutMs)
        this.cd = cd;

        this.cd.messagePort.onmessage = (m) => {
            this.listeners.forEach((v, _k) => {
                if (v.filter(m.data)) {
                    v.action(m.data)
                }
            })
        }
    }

    createUUID(): string {
        return uuidv4();
    }

    post(message: object): Promise<void> {
        this.cd.messagePort.postMessage(message);
        return Promise.resolve();
    }

    register(l: RegisterableListener): void {
        this.listeners.set(l.id!!, l)
    }

    unregister(id: string): void {
        this.listeners.delete(id)
    }

    createMeta(): object {
        return {
            "requestUuid": this.createUUID(),
            "timestamp": new Date(),
            "source": this.getSource()
        }
    }

    waitFor<X>(filter: (m: any) => boolean, timeoutErrorMessage?: string): Promise<X> {
        // console.log("Waiting for", filter, timeoutErrorMessage)
        return super.waitFor(filter, timeoutErrorMessage).then((v: any) => {
            // console.log("Wait over ", v, timeoutErrorMessage)
            return v;
        })
    }

    async disconnect(): Promise<void> {
        await super.disconnect()
        this.cd.messagePort.close()
    }
}

import { DesktopAgentDetails, WebDesktopAgentType, GetAgentParams, } from "@kite9/fdc3-standard";
import { RegisterableListener, AbstractMessaging } from "@kite9/fdc3-agent-proxy";
import { BrowserTypes } from "@kite9/fdc3-schema";
type WebConnectionProtocol5ValidateAppIdentitySuccessResponse = BrowserTypes.WebConnectionProtocol5ValidateAppIdentitySuccessResponse

export const DESKTOP_AGENT_SESSION_STORAGE_DETAILS_KEY = "fdc3-desktop-agent-details"

/**
 * Version of Messaging which is able to store details in the SessionState (i.e. works on the web)
 */
export abstract class AbstractWebMessaging extends AbstractMessaging {

    constructor(options: GetAgentParams, connectionAttemptUuid: string, timeout?: number) {
        super(options, connectionAttemptUuid, timeout)
    }

    abstract createUUID(): string
    abstract post(message: object): Promise<void>

    abstract register(l: RegisterableListener): void
    abstract unregister(id: string): void

    abstract createMeta(): object

    /**
     * Note that we also key by the window name as well, in case multiple iframes are using the same session storage.
     */
    private sessionKey(): string {
        const windowName = globalThis.window.name
        const keyName = windowName ? DESKTOP_AGENT_SESSION_STORAGE_DETAILS_KEY + "-" + windowName : DESKTOP_AGENT_SESSION_STORAGE_DETAILS_KEY
        return keyName
    }

    /**
     * Used to allow session-reconnection
     */
    storeInstanceUuid(vr: WebConnectionProtocol5ValidateAppIdentitySuccessResponse) {
        const details: DesktopAgentDetails = {
            agentType: WebDesktopAgentType.PROXY_PARENT,
            instanceUuid: vr.payload.instanceUuid,
            appId: vr.payload.appId,
            instanceId: vr.payload.instanceId,
        }

        globalThis.sessionStorage.setItem(this.sessionKey(), JSON.stringify(details))
    }

    /**
     * Stores the instanceUuid in session storage in case session needs reconnecting.
     */
    retrieveInstanceUuid(): string | undefined {
        const detailsStr = globalThis.sessionStorage.getItem(this.sessionKey())

        if (detailsStr) {
            const details = JSON.parse(detailsStr) as DesktopAgentDetails
            if (details.agentType == WebDesktopAgentType.PROXY_PARENT) {
                return details.instanceUuid
            }
        }

        return undefined;
    }

}

import { AppIntent } from "@kite9/fdc3-standard";
import { IntentResolver, IntentResolutionChoice } from '@kite9/fdc3-standard'
import { AbstractUIComponent } from "./AbstractUIComponent";
import { BrowserTypes } from "@kite9/fdc3-schema";
import { Context } from "@kite9/fdc3-context";

type IframeResolveAction = BrowserTypes.IframeResolveAction
type IframeResolve = BrowserTypes.IframeResolve

/**
 * Works with the desktop agent to provide a resolution to the intent choices.
 * This is the default implementation, but can be overridden by app implementers calling 
 * the getAgent() method
 */
export class DefaultDesktopAgentIntentResolver extends AbstractUIComponent implements IntentResolver {

    private pendingResolve: ((x: IntentResolutionChoice | void) => void) | null = null

    constructor(url: string | null) {
        super(url ?? "https://fdc3.finos.org/webui/intent_resolver.html", "FDC3 Intent Resolver")
    }

    async setupMessagePort(port: MessagePort): Promise<void> {
        await super.setupMessagePort(port)
        this.port = port

        this.port.addEventListener("message", (e) => {
            console.log("Got resolve action")
            if (e.data.type == 'iframeResolveAction') {
                const choice = e.data as IframeResolveAction
                if ((choice.payload.action == 'click') && (this.pendingResolve)) {
                    this.pendingResolve({
                        appId: choice.payload.appIdentifier!!,
                        intent: choice.payload.intent!!
                    })
                } else if ((choice.payload.action == 'cancel') && (this.pendingResolve)) {
                    this.pendingResolve()
                }

                this.pendingResolve = null
            }
        })
    }

    async chooseIntent(appIntents: AppIntent[], context: Context): Promise<IntentResolutionChoice | void> {
        const out = new Promise<IntentResolutionChoice | void>((resolve, _reject) => {
            this.pendingResolve = resolve
        })


        this.port?.postMessage({
            type: 'iframeResolve',
            payload: {
                appIntents,
                context
            }
        } as IframeResolve)

        return out
    }
}

